sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

    var parent_sys_id = '';
	var sgr = new GlideRecord('x_7872_digital_mar_blog'); 
	sgr.addQuery("name", source.u_blogurl);  
    sgr.query();  

    if (sgr.next()) {  
       log.info("sqr has rows");  
       parent_sys_id = sgr.sys_id;  
    }  

    var child_sys_id = '';
	var sgr = new GlideRecord('x_7872_digital_mar_blog'); 
	sgr.addQuery("blogurl", source.u_bloglinktourl);  
    sgr.query();  

    if (sgr.next()) {  
       log.info("sqr has rows");  
       child_sys_id = sgr.sys_id;  
    }  

	// Blog references Blog
	if (child_sys_id != '') {
		target.type = 'a8733340db90220078c0f6fdbf961971';
		target.parent = parent_sys_id;
		target.child = child_sys_id;
		target.connection_strength = 'Always';
	}


})(source, target, map, log, action==="update");
